import React, { useEffect, useState } from "react";
import CommonSection from "../shared/CommonSection";
import { BASE_URL } from "../utils/config";

export default function ManageBookings() {
  // const [list, setList] = useState([]);
  // const token = localStorage.getItem("token");

  // useEffect(() => {
  //   fetch(`${BASE_URL}/booking`, { headers: { Authorization: `Bearer ${token}` } })
  //     .then(r=>r.json()).then(setList);
  // }, []);

  // const request = async (bookingId, type) => {
  //   const body = { bookingId, type };
  //   if (type === "reschedule") {
  //     const d = prompt("Enter new date (YYYY-MM-DD):");
  //     if (!d) return;
  //     body.requestedDate = d;
  //   }
  //   await fetch(`${BASE_URL}/booking-requests`, {
  //     method: "POST",
  //     headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
  //     body: JSON.stringify(body)
  //   });
  //   alert("Request submitted.");
  // };


  const [list, setList] = useState([]);

useEffect(() => {
  const token = localStorage.getItem("token"); // adjust based on how you store it

  fetch(`${BASE_URL}/booking`, {
    headers: {
      "Authorization": `Bearer ${token}`
    }
  })
    .then(res => res.json())
    .then(data => {
      // If backend says unauthorized
      if (data.message === "Unauthorized") {
        setList([]); 
        return;
      }

      // If backend returns array of bookings
      if (Array.isArray(data)) {
        setList(data);
      } else if (Array.isArray(data.bookings)) {
        setList(data.bookings);
      } else {
        setList([]);
      }
    })
    .catch(err => {
      console.error("Error fetching bookings:", err);
      setList([]);
    });
}, []);




  return (
    <section>
      <CommonSection title={"Manage Bookings"} />
      <div className="container">
        <div className="row">
          {list.map(b => (
            <div key={b._id} className="col-12 mb-3">
              <div className="p-3 border rounded-3 d-flex justify-content-between align-items-center">
                <div>
                  <div><strong>{b.tourName || b.tour?.title}</strong></div>
                  <div>Status: {b.status}</div>
                  <div>Date: {b.date?.slice(0,10)}</div>
                </div>
                <div className="d-flex gap-2">
                  <button className="btn secondary__btn" onClick={()=>request(b._id, "reschedule")}>Reschedule</button>
                  <button className="btn primary__btn" onClick={()=>request(b._id, "cancel")}>Cancel</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
