import React, { useState } from "react";
import { BASE_URL } from "../utils/config";

const TourCard = ({ tour, wishlistIds = [] }) => {
  const [isFav, setIsFav] = useState(wishlistIds.includes(tour._id));

  const toggleWishlist = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please login to add to wishlist");
      return;
    }

    try {
      const res = await fetch(`${BASE_URL}/wishlist/toggle`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ tourId: tour._id }),
      });

      const data = await res.json();

      if (res.ok) {
        const fav = data.tours.some(t => t._id === tour._id);
        setIsFav(fav);
      } else {
        console.error(data.message);
      }
    } catch (e) {
      console.error("Wishlist toggle error:", e);
    }
  };

  return (
    <div className="tour-card border p-3 rounded shadow-sm">
      <h5>{tour.title}</h5>
      <p>{tour.city}</p>
      <button
        className={`btn ${isFav ? "btn-danger" : "btn-outline-danger"}`}
        onClick={toggleWishlist}
      >
        {isFav ? "♥ Remove" : "♡ Add to Wishlist"}
      </button>
    </div>
  );
};

export default TourCard;
