import React, { useEffect, useState } from "react";

const ManageBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setError("You are not authorized. Please login again.");
          return;
        }

        // const res = await fetch("http://localhost:4000/api/v1/booking", {
        const res = await fetch(`${BASE_URL}/booking`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });

        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }

        const data = await res.json();
        console.log("API Response:", data);

        // ✅ adjust based on your API response structure
        const bookingsData = data.data || data.bookings || data;
        console.log("Bookings to set:", bookingsData);

        setBookings(Array.isArray(bookingsData) ? bookingsData : []);
      } catch (err) {
        console.error("Error fetching bookings:", err);
        setError("Failed to load bookings. Please login again.");
        setBookings([]);
      }
    };

    fetchBookings();
  }, []);

  return (
    <div className="container">
      <h2>My Bookings</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {(!error && bookings.length === 0) && <p>No bookings found.</p>}
      <ul>
        {(bookings || []).map((b) => (
          <li key={b._id}>
            {b.tourName} - {new Date(b.createdAt).toLocaleDateString()}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageBookings;
