import React, { useRef, useEffect, useContext, useState } from 'react'
import { Container, Row, Button } from 'reactstrap'
import { Link, NavLink, useNavigate } from 'react-router-dom'

import logo from '../../assets/images/logo.png'
import './header.css'
import { AuthContext } from './../../context/AuthContext'

const Header = () => {
  const headerRef = useRef(null)
  const menuRef = useRef(null)
  const navigate = useNavigate()
  const { user, dispatch } = useContext(AuthContext)
  const [menuOpen, setMenuOpen] = useState(false)

  const logout = () => {
    dispatch({ type: 'LOGOUT' })
    navigate('/')
    setMenuOpen(false) // Close menu after logout
  }

  const toggleMenu = () => {
    setMenuOpen(!menuOpen)
  }

  const closeMenu = () => {
    setMenuOpen(false)
  }

  const stickyHeaderFunc = () => {
    window.addEventListener('scroll', () => {
      if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
        headerRef.current.classList.add('sticky__header')
      } else {
        headerRef.current.classList.remove('sticky__header')
      }
    })
  }

  useEffect(() => {
    stickyHeaderFunc();
    return window.removeEventListener('scroll', stickyHeaderFunc)
  })

  // Close menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target) && 
          !event.target.closest('.mobile__menu')) {
        setMenuOpen(false)
      }
    }

    document.addEventListener('mousedown', handleClickOutside)
    return () => {
      document.removeEventListener('mousedown', handleClickOutside)
    }
  }, [])

  const nav__links = [
    {
      path: '/home',
      display: 'Home'
    },
    {
      path: '/about',
      display: 'About'
    },
    {
      path: '/tours',
      display: 'Tours'
    },
    {
      path: '/wishlist',
      display: 'Wishlist'
    },
    {
      path: '/manage-bookings',
      display: 'My Bookings'
    },
    ...(!user ? [
      { path: '/login', display: 'Login' },
      { path: '/register', display: 'Register' }
    ] : [])
  ];

  return (
    <div className="headstyle">
      <header className="header" ref={headerRef}>
        <Container>
          <Row>
            <div className="nav__wrapper d-flex align-items-center justify-content-between">
              {/* =====logo===== */}
              <div className="logo">
                <img src={logo} alt="Logo" />
              </div>
              {/* =====logo end ===== */}

              {/* =====menu===== */}
              <div 
                className={`navigation ${menuOpen ? 'show__menu' : ''}`} 
                ref={menuRef}
              >
                <ul className="menu d-flex align-items-center gap-4">
                  {nav__links.map((item, index) => (
                    <li className="nav__item" key={index}>
                      <NavLink 
                        to={item.path}
                        className={navClass =>
                          navClass.isActive ? 'active__link' : ''
                        }
                        onClick={closeMenu} // Close menu when link is clicked
                      >
                        {item.display}
                      </NavLink>
                    </li>
                  ))}
                </ul>
              </div>
              {/* ===menu end=== */}

              <div className="nav__right d-flex align-items-center gap-4">
                {user && (
                  <div className='nav__btns d-flex align-items-center gap-2'>
                    <i className="ri-user-line"></i>
                    <div className="nav__btns d-flex align-items-center gap-4">
                      <h5 className='mb-0'>{user.username}</h5>
                      <Button className="btn btn-dark" onClick={logout}>
                        Logout
                      </Button>
                    </div>
                  </div>
                )}

                <span className="mobile__menu" onClick={toggleMenu}>
                  <i className={`ri-${menuOpen ? 'close' : 'menu'}-line`}></i>
                </span>
              </div>
            </div>
          </Row>
        </Container>
      </header>
    </div>
  )
}

export default Header
