import React, { useEffect, useState } from "react";
import axios from "axios";

const ManageBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        // Get token from localStorage (set at login)
        const token = localStorage.getItem("token");

        if (!token) {
          setError("You are not authorized. Please login.");
          return;
        }

        const res = await axios.get("http://localhost:4000/api/v1/booking", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setBookings(res.data.data);
      } catch (err) {
        console.error("Error fetching bookings:", err);

        if (err.response?.status === 403 || err.response?.status === 401) {
          setError("You are not authorized. Please login again.");
        } else {
          setError("Failed to fetch bookings.");
        }
      }
    };

    fetchBookings();
  }, []);

  return (
    <div className="manage-bookings">
      <h2>My Bookings</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}

      {!error && bookings.length === 0 && <p>No bookings found.</p>}

      <ul>
        {bookings.map((booking) => (
          <li key={booking._id}>
            <strong>Tour:</strong> {booking.tourName} <br />
            <strong>Date:</strong>{" "}
            {new Date(booking.date).toLocaleDateString()} <br />
            <strong>Status:</strong> {booking.status || "Confirmed"}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageBookings;
