import React, { useEffect, useState } from "react";
import axios from "axios";

const ManageBookings = () => {
  const [bookings, setBookings] = useState([]); // always starts as []
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setError("You are not authorized. Please login again.");
          return;
        }

        const res = await axios.get("http://localhost:4000/api/v1/booking", {
          headers: { Authorization: `Bearer ${token}` },
        });

        console.log("API Response:", res.data); // ðŸ‘€ log the response

        // âœ… only set array
        setBookings(Array.isArray(res.data?.data) ? res.data.data : []);
      } catch (err) {
        console.error("Error fetching bookings:", err);
        setError("Failed to load bookings. Please login again.");
        setBookings([]); // never undefined
      }
    };

    fetchBookings();
  }, []);

  return (
    <div className="container">
      <h2>My Bookings</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <ul>
        {(bookings || []).map((b) => (
          <li key={b._id}>
            {b.tourName} - {new Date(b.createdAt).toLocaleDateString()}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageBookings;
