import React, { useState } from 'react';
import '../styles/tour-card.css';
import { Card, CardBody } from 'reactstrap';
import { Link } from 'react-router-dom';
import calculateAvgRating from '../utils/avgRating';
import { priceWithDiscount } from '../utils/price';
import { BASE_URL } from "../utils/config";

const TourCard = ({ tour }) => {
  const [saved, setSaved] = useState(false); // wishlist state

  const toggleWishlist = async (e) => {
    e.preventDefault();
    e.stopPropagation();
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please login first");
      return;
    }
    try {
      const res = await fetch(`${BASE_URL}/api/v1/wishlist/toggle`, {
        method: "POST",
        headers: { 
          "Content-Type": "application/json", 
          Authorization: `Bearer ${token}` 
        },
        body: JSON.stringify({ tourId: tour._id })
      });
      if (res.ok) {
        setSaved(!saved); // update UI immediately
      }
    } catch (err) {
      console.error(err);
    }
  };

  const { _id, title, city, photo, price, featured, reviews } = tour;
  const { totalRating, avgRating } = calculateAvgRating(reviews);

  return (
    <div className="tour__card">
      <Card>
        <div className="tour__img">
          <img src={photo} alt="tour-img" />
          {featured && <span>Featured</span>}
        </div>
        <CardBody>
          <div className="card__top d-flex align-items-center justify-content-between">
            <span className="tour__location d-flex align-items-center gap-1">
              <i className="ri-map-pin-line"></i> {city}
            </span>
            <span className="tour__rating d-flex align-items-center gap-1">
              <i className="ri-star-fill"></i>
              {avgRating === 0 ? null : avgRating}
              {totalRating === 0 ? (
                " Not rated"
              ) : (
                <span>({reviews.length})</span>
              )}
            </span>
          </div>

          <h5 className="tour__title">
            <Link to={`/tours/${_id}`}>{title}</Link>
          </h5>

          <div className="card__bottom d-flex align-items-center justify-content-between mt-3">
            <h5>
              {priceWithDiscount(tour.price, tour.discountPercent).original
                ? priceWithDiscount(tour.price, tour.discountPercent).final
                : tour.price}{" "}
              <span>/per person</span>
            </h5>
            <button className="btn booking__btn">
              <Link to={`/tours/${_id}`}>Book Now</Link>
            </button>
          </div>

          {/* Wishlist button */}
          <button 
            className={`btn w-100 mt-2 ${saved ? "btn-success" : "secondary__btn"}`} 
            onClick={toggleWishlist}
          >
            {saved ? "❤️ Saved" : "♡ Save"}
          </button>
        </CardBody>
      </Card>
    </div>
  );
};

export default TourCard;
