import React, { useEffect, useState } from "react";
import CommonSection from "../shared/CommonSection";
import TourCard from "../shared/TourCard";
import { BASE_URL } from "../utils/config";

export default function Wishlist() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");
    fetch(`${BASE_URL}/wishlist`, { headers: { Authorization: `Bearer ${token}` } })
      .then(r=>r.json()).then(data => setItems(data?.tours || []));
  }, []);

  return (
    <section>
      <CommonSection title={"Your Wishlist"} />
      <div className="container">
        <div className="row">
          {items.length === 0 && <p className="text-center mt-4">No favorites yet.</p>}
          {items.map(t => (
            <div className="col-lg-3 col-md-6 mb-4" key={t._id}>
              <TourCard tour={t} />
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
